import React, { useEffect, useState, useRef } from "react";
import io from "socket.io-client";
import "../styles/messages.css";
const socket = io("http://localhost:5000");

interface Message {
  sender: string;
  text: string;
}

export default function Messages({ username }: { username: string }) {
  const [onlineUsers, setOnlineUsers] = useState<string[]>([]);
  const [currentChat, setCurrentChat] = useState<string | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [text, setText] = useState("");
  const messagesEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    socket.emit("login", username);

    socket.on("onlineUsers", (users: string[]) => {
      setOnlineUsers(users.filter(u => u !== username));
    });

    socket.on("receiveMessage", (msg: Message) => {
      setMessages(prev => [...prev, msg]);
    });

    return () => {
      socket.off("onlineUsers");
      socket.off("receiveMessage");
    };
  }, [username]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = () => {
    if (!text || !currentChat) return;

    // Emit message to server
    socket.emit("privateMessage", { toSocketId: Object.keys(socket.io.engine.clients).find(id => id !== socket.id), text });
    setText("");
  };

  return (
    <div className="chat-container">
      <div className="user-list">
        <h4>المتصلون</h4>
        {onlineUsers.map(user => (
          <div
            key={user}
            className={`user ${user === currentChat ? "active" : ""}`}
            onClick={() => {
              setCurrentChat(user);
              setMessages([]);
            }}
          >
            {user}
          </div>
        ))}
      </div>

      <div className="chat-area">
        <h4>{currentChat ? `الدردشة مع ${currentChat}` : "اختر مستخدم للشات"}</h4>
        <div className="messages">
          {messages.map((m, i) => (
            <div key={i} className={`message ${m.sender === username ? "sent" : "received"}`}>
              <span className="sender">{m.sender}:</span> {m.text}
            </div>
          ))}
          <div ref={messagesEndRef}></div>
        </div>
        <div className="input-area">
          <input
            type="text"
            value={text}
            onChange={e => setText(e.target.value)}
            placeholder="اكتب رسالة..."
            onKeyDown={e => e.key === "Enter" && sendMessage()}
          />
          <button onClick={sendMessage}>إرسال</button>
        </div>
      </div>
    </div>
  );
}
