import React, { useEffect, useState } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

interface Message {
  sender: string;
  text: string;
}

export default function Messages({ username }: { username: string }) {
  const [onlineUsers, setOnlineUsers] = useState<string[]>([]);
  const [currentChat, setCurrentChat] = useState<string | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [text, setText] = useState("");

  useEffect(() => {
    socket.emit("login", username);

    socket.on("onlineUsers", (users: string[]) => {
      setOnlineUsers(users.filter(u => u !== username));
    });

    socket.on("receiveMessage", (msg: Message) => {
      setMessages(prev => [...prev, msg]);
    });

    return () => {
      socket.off("onlineUsers");
      socket.off("receiveMessage");
    };
  }, [username]);

  const sendMessage = () => {
    if (!text || !currentChat) return;
    const toSocketId = Object.keys(socket.io.engine.clients).find(
      id => id !== socket.id && onlineUsers.includes(currentChat)
    );
    if (toSocketId) {
      socket.emit("privateMessage", { toSocketId, text });
    }
    setText("");
  };

  return (
    <div style={{ display: "flex", gap: "20px" }}>
      <div style={{ width: "150px", borderRight: "1px solid gray" }}>
        <h4>Online Users</h4>
        {onlineUsers.map(user => (
          <div
            key={user}
            style={{ cursor: "pointer", padding: "5px", background: user === currentChat ? "#ddd" : "#fff" }}
            onClick={() => {
              setCurrentChat(user);
              setMessages([]);
            }}
          >
            {user}
          </div>
        ))}
      </div>

      <div style={{ flex: 1 }}>
        <h4>Chat with {currentChat || "..."}</h4>
        <div style={{ height: "400px", overflowY: "auto", border: "1px solid gray", padding: "5px" }}>
          {messages.map((m, i) => (
            <div key={i}><b>{m.sender}:</b> {m.text}</div>
          ))}
        </div>
        <input
          value={text}
          onChange={e => setText(e.target.value)}
          placeholder="اكتب رسالة..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}
