import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import "../styles/Messages.css";

const socket = io("http://localhost:5000");

const Messages: React.FC<{ username: string }> = ({ username }) => {
  const [onlineUsers, setOnlineUsers] = useState<string[]>([]);
  const [selectedUser, setSelectedUser] = useState<string | null>(null);
  const [messages, setMessages] = useState<{ sender: string; text: string }[]>([]);
  const [text, setText] = useState("");

  useEffect(() => {
    socket.emit("join", username);

    socket.on("onlineUsers", (users: string[]) => setOnlineUsers(users));

    socket.on("receiveMessage", (message: { sender: string; text: string }) => {
      setMessages(prev => [...prev, message]);
    });

    return () => {
      socket.disconnect();
    };
  }, [username]);

  const sendMessage = () => {
    if (selectedUser && text.trim()) {
      socket.emit("sendMessage", { sender: username, receiver: selectedUser, text });
      setMessages(prev => [...prev, { sender: username, text }]);
      setText("");
    }
  };

  return (
    <div className="messages-container">
      <div className="users-list">
        <h3>المستخدمين المتصلين</h3>
        {onlineUsers.map(user => (
          <div
            key={user}
            className={`user ${selectedUser === user ? "active" : ""}`}
            onClick={() => setSelectedUser(user)}
          >
            {user}
          </div>
        ))}
      </div>
      <div className="chat-box">
        <h3>{selectedUser ? `الدردشة مع ${selectedUser}` : "اختر مستخدم للدردشة"}</h3>
        <div className="messages">
          {messages
            .filter(msg => msg.sender === selectedUser || msg.sender === username)
            .map((msg, i) => (
              <div key={i} className={`message ${msg.sender === username ? "mine" : "theirs"}`}>
                <strong>{msg.sender}: </strong>{msg.text}
              </div>
            ))}
        </div>
        {selectedUser && (
          <div className="input-area">
            <input
              type="text"
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="اكتب رسالة..."
            />
            <button onClick={sendMessage}>إرسال</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Messages;
